    <script>
        function updateTimeDate() {
            var now = new Date();
            var options = { 
                weekday: 'long', 
                year: 'numeric', 
                month: 'long', 
                day: 'numeric', 
                hour: 'numeric', 
                minute: 'numeric', 
                second: 'numeric', 
                hour12: true 
            };
            var formattedDateTime = now.toLocaleDateString('en-US', options);
            document.getElementById('time-date').textContent = formattedDateTime;
        }

        setInterval(updateTimeDate, 1000);
    </script>


